import {Tag} from '../../../common/cards/Tag';
import {CardName} from '../../../common/cards/CardName';
import {CardRenderer} from '../render/CardRenderer';
import {CardResource} from '../../../common/CardResource';
import {IActionCard} from '../ICard';
import {PreludeCard} from '../prelude/PreludeCard';
import {IPlayer} from '../../IPlayer';
import {OrOptions} from '../../inputs/OrOptions';
import {SelectCard} from '../../inputs/SelectCard';
import {AndOptions} from '../../inputs/AndOptions';
import {SelectAmount} from '../../inputs/SelectAmount';
import {SelectResource} from '../../inputs/SelectResource';
import {Units} from '../../../common/Units';

export class FloatingTradeHub extends PreludeCard implements IActionCard {
  constructor() {
    super({
      name: CardName.FLOATING_TRADE_HUB,
      tags: [Tag.SPACE],
      resourceType: CardResource.FLOATER,

      metadata: {
        cardNumber: 'P49',
        renderData: CardRenderer.builder((b) => {
          b.action('Add 2 floaters to ANY card.', (ab) => ab.startAction.resource(CardResource.FLOATER, 2).asterix()).br;
          b.action('Remove any number of floaters here to gain that many of one standard resource.', (ab) => {
            ab.text('X').resource(CardResource.FLOATER).startAction.text('X').wild(1);
          }).br;
        }),
      },
    });
  }

  public canAct(): boolean {
    return true;
  }

  public action(player: IPlayer) {
    const add2Floaters = new SelectCard('Select card to gain 2 floaters', undefined, player.getResourceCards(CardResource.FLOATER)).andThen(([card]) => {
      player.addResourceTo(card, {qty: 2, log: true});
      return undefined;
    });
    const selectResource = new SelectResource('Select resource to gain');
    const selectAmount = new SelectAmount('Select amount of floaters to remove', undefined, 1, this.resourceCount, true);
    const removeFloaters = new AndOptions(selectAmount, selectResource);
    removeFloaters.title = 'Convert floaters to standard resources';
    removeFloaters.andThen(() => {
      // TODO(kberg): Add a better log message.
      player.removeResourceFrom(this, selectAmount.selected, {log: true});
      player.stock.add(Units.ResourceMap[selectResource.selected], selectAmount.selected, {log: true, from: this});
      return undefined;
    });
    if (this.resourceCount === 0) {
      return add2Floaters;
    }
    return new OrOptions(add2Floaters, removeFloaters);
  }
}

import {CardName} from '../../../common/cards/CardName';
import {CardRenderer} from '../render/CardRenderer';
import {PreludeCard} from '../prelude/PreludeCard';
import {IPlayer} from '../../IPlayer';
import {IActionCard} from '../ICard';
import {Units} from '../../../common/Units';
import {sum} from '../../../common/utils/utils';
import {AndOptions} from '../../inputs/AndOptions';
import {SelectCard} from '../../inputs/SelectCard';
import {SelectResource} from '../../inputs/SelectResource';
import {PathfindersExpansion} from '../../pathfinders/PathfindersExpansion';

export class FocusedOrganization extends PreludeCard implements IActionCard {
  constructor() {
    super({
      name: CardName.FOCUSED_ORGANIZATION,

      behavior: {
        drawCard: 1,
        standardResource: 1,
      },

      metadata: {
        cardNumber: 'P50',
        renderData: CardRenderer.builder((b) => {
          b.action('Discard 1 card and spend 1 standard resource to draw 1 card and gain 1 standard resource.', (ab) => {
            ab.cards(1).wild(1).startAction.cards(1).wild(1);
          }).br;
          b.cards(1).wild(1).br;
          b.plainText('Draw 1 card and gain 1 standard resource.');
        }),
      },
    });
  }

  public canAct(player: IPlayer): boolean {
    return player.cardsInHand.length > 0 && sum(Units.values(player.stock)) > 0;
  }

  public action(player: IPlayer) {
    const discardableStandardResources = Units.keys.filter((type) => player.stock[type] > 0);
    return new AndOptions(
      new SelectResource('Select resource to discard', discardableStandardResources)
        .andThen((type) => {
          player.stock.deduct(Units.ResourceMap[type], 1, {log: true});
          if (type === 'megacredits' || type === 'steel' || type === 'titanium') {
            PathfindersExpansion.addToSolBank(player);
          }
          return undefined;
        }),
      new SelectCard('Select card to discard', 'select', player.cardsInHand)
        .andThen(([card]) => {
          player.discardCardFromHand(card);
          return undefined;
        })).andThen(() => {
      player.drawCard();
      return new SelectResource('Select resource to gain')
        .andThen((type) => {
          player.stock.add(Units.ResourceMap[type], 1, {log: true});
          return undefined;
        });
    });
  }
}

