    if (cards.length === 0) {
      return undefined;
    }
    const input = new SelectCard(
      'Select card to gain 2 floaters or 3 data',
      'Add resources',
      cards)
      .andThen(([card]) => {
        if (card.resourceType === CardResource.FLOATER) {
          player.addResourceTo(card, {qty: 2, log: true});
        } else {
          player.addResourceTo(card, {qty: 3, log: true});
        }
        return undefined;
      });

    if (cards.length === 1) {
      input.cb(cards);
      return undefined;
    }
    return input;
  }
}

