  public override getCardDiscount(player: IPlayer, card: IProjectCard) {
    if (player.isCorporation(CardName.ADHAI_HIGH_ORBIT_CONSTRUCTIONS) && this.matchingTags(card.tags)) {
      return Math.floor(this.resourceCount / 1);
    } else {
      return 0;
    }
  }
