      name: CardName.GEOSCAN_SATELLITE,
      cost: 6,
      tags: [Tag.SCIENCE, Tag.SPACE],

      behavior: {
        addResourcesToAnyCard: {count: 2, type: CardResource.DATA},
      },

      metadata: {
        cardNumber: 'U02',
        renderData: CardRenderer.builder((b) => {
          b.resource(CardResource.DATA, 2).geoscan();
